// components/media/media-uploader.tsx
import { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { Progress } from '@/components/ui/progress';
import { Button } from '@/components/ui/button';
import { Upload, X } from 'lucide-react';

export function MediaUploader({ onUploadComplete }) {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState({});

  const onDrop = useCallback(acceptedFiles => {
    setFiles(prev => [...prev, ...acceptedFiles.map(file => 
      Object.assign(file, { preview: URL.createObjectURL(file) })
    )]);
  }, []);

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif'],
    },
    maxSize: 10485760, // 10MB
  });

  const uploadFile = async (file) => {
    try {
      // 1. Presigned URL ìš”ì²­
      const response = await fetch('/api/media/presigned-url', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          fileName: file.name,
          contentType: file.type,
        }),
      });

      if (!response.ok) throw new Error('Presigned URL ìš”ì²­ ì‹¤íŒ¨');

      const { url, fields, fileUrl } = await response.json();

      // 2. í¼ ë°ì´í„° ìƒì„±
      const formData = new FormData();
      Object.entries(fields).forEach(([key, value]) => {
        formData.append(key, value);
      });
      formData.append('file', file);

      // 3. íŒŒì¼ ì—…ë¡œë“œ
      const uploadResponse = await fetch(url, {
        method: 'POST',
        body: formData,
        // ì—…ë¡œë“œ ì§„í–‰ ìƒíƒœ ì¶”ì 
        onUploadProgress: (event) => {
          const percentCompleted = Math.round((event.loaded * 100) / event.total);
          setProgress(prev => ({ ...prev, [file.name]: percentCompleted }));
        },
      });

      if (!uploadResponse.ok) throw new Error('íŒŒì¼ ì—…ë¡œë“œ ì‹¤íŒ¨');

      return fileUrl;
    } catch (error) {
      console.error('ì—…ë¡œë“œ ì˜¤ë¥˜:', error);
      throw error;
    }
  };

  const handleUpload = async () => {
    if (!files.length || uploading) return;

    setUploading(true);

    try {
      const uploadedUrls = await Promise.all(files.map(uploadFile));
      onUploadComplete(uploadedUrls);
      setFiles([]);
    } catch (error) {
      console.error('ì—…ë¡œë“œ ì˜¤ë¥˜:', error);
    } finally {
      setUploading(false);
      setProgress({});
    }
  };

  // ì»´í¬ë„ŒíŠ¸ ë Œë”ë§ ë¡œì§
}
